INSERT INTO `cis_tce_dsce`.`program_outcomes` (`po_description`) VALUES ('Environment and sustainability: Understand the impact of the professional engineering solutions in societal and environmental contexts, and demonstrate the knowledge of, and need for sustainable development');
INSERT INTO `cis_tce_dsce`.`program_outcomes` (`po_description`) VALUES ('Ethics: Apply ethical principles and commit to professional ethics and responsibilities and norms of the engineering practice');
INSERT INTO `cis_tce_dsce`.`program_outcomes` (`po_description`) VALUES ('Individual and team work: Function effectively as an individual, and as a member or leader in diverse teams, and in multidisciplinary settings');
INSERT INTO `cis_tce_dsce`.`program_outcomes` (`po_description`) VALUES ('Communication: Communicate effectively on complex engineering activities with the engineering community and with society at large, such as, being able to comprehend and write effective reports and design documentation, make effective presentations, and give and receive clear instructions');
INSERT INTO `cis_tce_dsce`.`program_outcomes` (`po_description`) VALUES ('Project management and finance: Demonstrate knowledge and understanding of the engineering and management principles and apply these to one’s own work, as a member and leader in a team, to manage projects and in multidisciplinary environments');
INSERT INTO `cis_tce_dsce`.`program_outcomes` (`po_description`) VALUES ('Life-long learning: Recognize the need for, and have the preparation and ability to engage in independent and life-long learning in the broadest context of technological change');
